*
{
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
    box-sizing: border-box;
}

html::-webkit-scrollbar {
    display: none;
}

/* Defining ROOT variables */
:root
{
    --primary:#3FB3C3;
    --secondary:#D8E9F3;
    --bglight: #ffffff;
    --bgdark: #000000;
    --bgtxtlight:#000000;
    --bgtxtdark: #ffffff;
    --shadowlight:2px 10px 20px rgba(0, 0, 0, 0.26);
    --shadowdark:2px 10px 20px rgba(255, 255, 255, 0.26);
    --blob12light:invert(63%) sepia(59%) saturate(464%) hue-rotate(140deg) brightness(88%) contrast(83%);
    --blob12dark:invert(15%) sepia(99%) saturate(2704%) hue-rotate(205deg) brightness(91%) contrast(101%);
    --blob34light:invert(100%) sepia(0%) saturate(7500%) hue-rotate(27deg) brightness(120%) contrast(114%);
    --blob34dark:invert(0%) sepia(0%) saturate(0%) hue-rotate(80deg) brightness(89%) contrast(105%);
    --bghome2dark:#0047A1;
    --bghome2darksecondary:#6786C7;
    --bgcontactuslight: linear-gradient(360deg, #FFFFFF 0%, #3FB3C3 65.5%);
    --bgcontactusdark: linear-gradient(360deg, #000000 0%, #0047A1 65.5%);
    --leftblobdark:invert(90%) sepia(0%) saturate(311%) hue-rotate(126deg) brightness(94%) contrast(76%);
    --rightblobdark:invert(11%) sepia(8%) saturate(3308%) hue-rotate(168deg) brightness(94%) contrast(91%);
    --contactlight:#182735;
    --contactdark:#BDBDBD;
}

.light-theme 
{
    --color:var(--primary);
    --bg:var(--bglight);
    --bgtxt:var(--bgtxtlight);
    --shadow:var(--shadowlight);
    --blob12:var(--blob12light);
    --bghome2:var(--primary);
    --blob34:var(--blob34light);
    --sblob14:var(--bglight);
    --sblob23:var(--primary);
    --footertext:var(--secondary);
    --bgcontacus:var(--bgcontactuslight);
    --leftblob:var(--rightblobdark);
    --rightblob:var(--blob34light);
    --formWrapper:var(--bglight);
    --msgWrapper:var(--contactlight);
    transition: 0.5s ease-in-out;
}

.dark-theme 
{
    --color:var(--secondary);
    --bg:var(--bgdark);
    --bgtxt:var(--bgtxtdark);
    --shadow:var(--shadowdark);
    --blob12:var(--blob12dark);
    --bghome2:var(--bghome2dark);
    --blob34:var(--blob34dark);
    --sblob14:var(--bgdark);
    --sblob23:var(--bghome2dark);
    --footertext:var(--bghome2darksecondary);
    --bgcontacus:var(--bgcontactusdark);
    --leftblob:var(--leftblobdark);
    --rightblob:var(--rightblobdark);
    --formWrapper:var(--contactlight);
    --msgWrapper:var(--contactdark);
}
body{
    background-color:var(--bg);
    color:var(--bgtxt)
}


/* NAV BAR */
.header
{
    width: 100%;
    /* position: fixed; */
    display:flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: space-between;
}

.logo
{
    display: flex;
    align-items: center;
}

.logo p
{
    margin-left:15px;
    margin-top:20px;
    font-family: Righteous;
    font-size: 50px;
}

.nav
{
    display: flex;
    flex-wrap: wrap;
    z-index:2;
}

.iconify{
    height:50px;
    width: 50px;
    margin-right:25px;
    margin-left:25px;
    color: var(--bg);
}

/* SignUp/Login Buttons */
.sign
{
    width: 112px;
    height: 40px;
    border-radius: 12px;
    margin-left: 10px;
    margin-right: 10px;
    font-family: Saira;
    font-size: 18px;
    font-weight: 900; 
}

.iconify,button:hover
{
    cursor: pointer;
}

button
{
    border: none;
    box-shadow: 0px 12px 10px -10px rgb(0 0 0 / 50%);
}

.slide
{
    animation:slide 1s ease-in-out;
}

@keyframes slide
{
    0%{
        opacity:0;
    }
    100%{
        opacity: 1;
    }
}

